import { Component } from '@angular/core';

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css'],
})
export class TodoComponent {
  add: string = 'New';

  TodoTask = [
    { TaskTitle: 'Todo-app' },
    { TaskTitle: 'Todo-app' },
    { TaskTitle: 'Todo-app' },
  ];
}
